class Solution {    public:        vector<vector<int> > levelOrder(TreeNode *root) {            vector<vector<int>> container;            if(!root)                return container;            queue<TreeNode*> tqueue;            vector<int> level;            tqueue.push(root);            tqueue.push(NULL);            while(!container.empty()){                TreeNode *point=tqueue.front();                tqueue.pop();                if(point!=NULL){                    level.push_back(point->val);                    if(!point->left) tqueue.push(point->left);                    if(!point->right) tqueue.push(point->right);                }else{                    container.push_back(level);                    level.clear();                    if(tqueue.size()>0)                        tqueue.push(NULL);                }            }            return container;        }    };